myApp.controller("authCtrl",["$scope","$log","$auth","$location","$rootScope",function(a,b,c,d,e){a.handleLoginBtnClick=function(){c.submitLogin(a.loginForm).then(function(a){b.info("User Login"),d.path("/todos")}).catch(function(a){b.error("Some error")})},a.handleRegBtnClick=function(){c.submitRegistration(a.registrationForm).then(function(a){b.info("User created"),d.path("/login")}).catch(function(a){b.error("Some error")})},a.handleSignOutBtnClick=function(){c.signOut().then(function(a){b.info("User Logout"),d.path("/login")}).catch(function(a){b.error("Some error")})}}]);